async function loadLatestAssets() {
  try {
    // Headshot
    const headshotRes = await fetch("/api/get-latest?type=headshot");
    const headshotData = await headshotRes.json();
    if (headshotData.url) {
      document.getElementById("headshot").src = headshotData.url;
    }

    // CV
    const cvRes = await fetch("/api/get-latest?type=cv");
    const cvData = await cvRes.json();
    if (cvData.url) {
      document.getElementById("cv-download-link").href = cvData.url;
    }

    // Video (optional)
    const videoRes = await fetch("/api/get-latest?type=video");
    const videoData = await videoRes.json();
    if (videoData.url) {
      document.getElementById("intro-video").src = videoData.url;
    }
  } catch (err) {
    console.error("Asset loader failed:", err);
  }
}

document.addEventListener("DOMContentLoaded", loadLatestAssets);
